graphql.query:
mutation confirmEmail ($token: String!) {
    confirmEmail (token: $token) {
        id
        name
        username
        email
        avatarUrl
        token
        usertag
        admin
        ownBikes {
            bikeId
            bikename
            thumbUrl
            prodStartYear
        }
        followBikes {
            bikeId
            bikename
            thumbUrl
            prodStartYear
        }
    }
}

mutation deletePost ($postId: ID!) {
    deletePost (postId: $postId)
}

mutation deleteComment ($commentId: ID!) {
    deleteComment (commentId: $commentId)
}

mutation likePost ($postId: ID!) {
    likePost (postId: $postId)
}

mutation likeComment ($commentId: ID, $postId: ID) {
    likeComment (commentId: $commentId, postId: $postId) {
        id
      replyToId
      postId
      username
      avatarUrl
      userId
      userbikes
      createdAt
      body
      replies {
        id
        replyToId
        username
        avatarUrl
        userId
        userbikes
        body
        likes {
          username
          id
          avatarUrl
        }
        likeCount
        dislikes {
          username
          id
          avatarUrl
        }
        dislikeCount
      }
      likes {
        username
        id
        avatarUrl
      }
      likeCount
      dislikes {
        username
        id
        avatarUrl
      }
      dislikeCount
    }
}

mutation createContest ($title: String!, $sponsors: [String!]!, $imageUrl: String!, $closingDate: String!, $isActive: Boolean!) {
    createContest (title: $title, sponsors: $sponsors, imageUrl: $imageUrl, closingDate: $closingDate, isActive: $isActive) {
        id
        title
        createdAt
        sponsors
        imageUrl
        closingDate
        isActive
        photos {
            username
            imageUrl
            thumbUrl
            bikeId
            bikename
            bikethumbUrl
        }
    }
}

mutation addPhotoToContest ($contestId: ID!, $imageUrl: String!, $thumbUrl: String!, $bikeId: String!, $bikename: String!, $bikethumbUrl: String!) {
    addPhotoToContest (contestId: $contestId, imageUrl: $imageUrl, thumbUrl: $thumbUrl, bikeId: $bikeId, bikename: $bikename, bikethumbUrl: $bikethumbUrl) {
        username
        imageUrl
        thumbUrl
        bikeId
        bikename
        bikethumbUrl
    }
}

mutation addPreviewPhotoToContest ($contestId: ID!, $imageUrl: String!, $thumbUrl: String!) {
    addPreviewPhotoToContest (contestId: $contestId, imageUrl: $imageUrl, thumbUrl: $thumbUrl) {
        username
        imageUrl
        thumbUrl
        bikeId
        bikename
        bikethumbUrl
    }
}

mutation deletePhotoContestImage ($contestId: ID!, $imageUrl: String!) {
    deletePhotoContestImage (contestId: $contestId, imageUrl: $imageUrl) {
        username
        imageUrl
        thumbUrl
        bikeId
        bikename
        bikethumbUrl
    }
}

mutation s3Sign ($files: [filesForS3Signature!]) {
    s3Sign (files: $files) {
        filename
        signedRequest
        url
    }
}

mutation createSharedLink ($url: String!, $title: String!, $imageUrl: String, $description: String!, $type: String!) {
    createSharedLink (url: $url, title: $title, imageUrl: $imageUrl, description: $description, type: $type) {
        url
        title
        description
        imageUrl
        type
    }
}

mutation createFeedback ($pageLink: String!, $description: String!) {
    createFeedback (pageLink: $pageLink, description: $description) {
        pageLink
        description
        username
    }
}

mutation resendEmail ($userId: ID) {
    resendEmail (userId: $userId)
}

query getBikes ($first: Int, $after: ID, $search: String) {
    getBikes (first: $first, after: $after, search: $search) {
        edges {
            node {
                id
                bikeId
                bikename
                description
                storyUrl
                isActive
                pictureUrls
                brand
                category
                thumbUrl
                prodStartYear
                prodEndYear
                createdAt
            }
            cursor
        }
        pageInfo {
            hasNextPage
            endCursor
            startCursor
        }
    }
}

query getContests {
    getContests {
        id
        title
        createdAt
        sponsors
        imageUrl
        closingDate
        isActive
        photos {
            username
            imageUrl
            thumbUrl
            bikeId
            bikename
            bikethumbUrl
        }
    }
}

query getContest ($contestId: ID!) {
    getContest (contestId: $contestId) {
        id
        title
        createdAt
        sponsors
        imageUrl
        closingDate
        isActive
        photos {
            username
            imageUrl
            thumbUrl
            bikeId
            bikename
            bikethumbUrl
        }
    }
}

query getLink ($url: String!) {
    getLink (url: $url) {
        url
        title
        description
        imageUrl
        type
    }
}

query getLinks {
    getLinks {
        url
        title
        description
        imageUrl
        type
    }
}
